import React, {useState, useEffect} from 'react';
import {Text, View, StyleSheet, Image, FlatList, Alert} from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import axios from 'axios';
import {Button} from 'react-native-paper';
import Delete from 'react-native-vector-icons/MaterialIcons';
function AdminScreen({navigation}) {
  const [userData, setUserData] = useState('');
  const [allUserData, setAllUserData] = useState('');

  async function getAllData() {
    console.log('workifn')
    axios.get('http://192.168.1.100:5001/get-all-user').then(res => {
      console.log("get all userdata",res.data);

      setAllUserData(res.data.data);
    });
  }

  async function getData() {
    const token = await AsyncStorage.getItem('token');
    console.log(token);
    axios
      .post('http://192.168.1.100:5001/userdata', {token: token})
      .then(res => {
        console.log(res.data);
        setUserData(res.data.data);
      });
  }
  useEffect(() => {
    getData();
    getAllData();
    console.log('weoek')
  }, []);
  function signOut() {
    AsyncStorage.setItem('isLoggedIn', '');
    AsyncStorage.setItem('token', '');
    AsyncStorage.setItem('userType', '');
    navigation.navigate('Login');
  }


  function deleteUser(data) {
    axios
      .post('http://192.168.1.100:5001/delete-user', {id: data._id})
      .then(res => {
        console.log(res.data);
        if(res.data.status=="Ok"){
          Alert.alert("User deleted");
          getAllData();
        }
      });
  }

  const UserCard=({data})=>(
    <View style={styles.card}>
          <Image source={{uri:
             data.image==""|| data.image==null
             ? 'data:image/png;base64,'
             : data.image,
          
          }} style={styles.image} />
          <View style={styles.cardDetails}>
            <Text style={styles.name}>{data.name}</Text>
            <Text style={styles.email}>{data.email}</Text>
            <Text style={styles.userType}>{data.userType}</Text>
          </View>
          <View>
            <Delete name="delete" size={30} onPress={() =>  deleteUser(data)}/>
          </View>
        </View>
  )
  return (
    <>
      <View style={styles.container}>
        <View style={styles.userInfo}>
          <Text style={styles.userName}>{userData.name} </Text>
          <Text style={styles.userType}>{userData.userType}</Text>
         <Text style={styles.userType}>Total User:{allUserData.length}</Text>
        </View>
        <FlatList data={allUserData}
        keyExtractor={item => item._id}
        renderItem={({item})=>(<UserCard data={item}/>)}
        
        />
        
      </View>
      <Button
        mode="contained"
        onPress={() => signOut()}
        style={{
          backgroundColor: '#0163d2',
          width: '100%',
          borderRadius: 0,
          margin: 0,
        }}
        labelStyle={{fontSize: 16}}>
        Log Out
      </Button>
    </>
  );
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
    backgroundColor: '#f5f5f5',
  },
  userInfo: {
    marginBottom: 20,
  },
  userName: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#333333',
    marginBottom: 5,
  },
  userType: {
    fontSize: 18,
    color: '#777777',
  },
  card: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#ffffff',
    padding: 15,
    borderRadius: 8,
    marginBottom: 10,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  image: {
    width: 50,
    height: 50,
    borderRadius: 25,
    marginRight: 10,
  },
  cardDetails: {
    flex: 1,
  },
  name: {
    fontSize: 18,
    fontWeight: 'bold',
    color: '#333333',
    marginBottom: 5,
  },
  email: {
    fontSize: 14,
    color: '#777777',
  },
});
export default AdminScreen;