import {useState, useEffect} from 'react';
import {StyleSheet, Text, View, FlatList, Image, TextInput} from 'react-native';
import axios from 'axios';
import Icon from 'react-native-vector-icons/FontAwesome';
function UserScreen() {
  const [allUserData, setAllUserData] = useState('');
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');

  async function getAllData() {
    axios.get('http://192.168.1.100:5001/get-all-user').then(res => {
      console.log(res.data);

      setAllUserData(res.data.data);
    });
  }

  function handleChange(query) {
    setSearchQuery(query);
    console.log("first", allUserData)
    const filtered = allUserData.filter(user =>
      user.name.toLowerCase().includes(query.toLowerCase()) ||
      user.email.toLowerCase().includes(query.toLowerCase()) ||
      user?.userType?.toLowerCase().includes(query?.toLowerCase())

    );
    setFilteredUsers(filtered);
  }

  useEffect(() => {
    getAllData();
  }, []);
  const UserCard = ({data}) => (
    <View style={styles.card}>
      <Image
        source={{
          uri:
            data.image == '' || data.image == null
              ? 'data:image/png;base64,'
              : data.image,
        }}
        a
        style={styles.image}
      />
      <View style={styles.cardDetails}>
        <Text style={styles.name}>{data.name}</Text>
        <Text style={styles.email}>{data.email}</Text>
        <Text style={styles.userType}>{data.userType}</Text>
      </View>
      <View></View>
    </View>
  );
  return (
    <View style={styles.container}>
      <View style={styles.searchBar}>
        <View style={styles.searchIcon}>
          <Icon name="search" size={18} />
          <TextInput
            style={styles.searchInput}
            placeholder="Search name or email"
            placeholderTextColor={'#707070'}
            value={searchQuery}
            onChangeText={handleChange}
          />
        </View>
        <Text style={styles.recordsText}>
         {searchQuery.length>0?`${filteredUsers.length} records found`:`Total Records ${allUserData.length} `}
        </Text>
      </View>
      <FlatList
        data={searchQuery.length > 0 ? filteredUsers : allUserData}
        showsVerticalScrollIndicator={false}
        keyExtractor={item => item._id}
        renderItem={({item}) => <UserCard data={item} />}
      />
    </View>
  );
}
